    private void tabopActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tabopActionPerformed
        if (itemTab.getSelectedIndex() == 1) { // Verifica qual tab está selecionada.
            Mochila usarcura = new Mochila(); // Se estiver na tab de poções, botão usa cura.
            usarcura.usarcura();
        } else {
            Mochila venderitem = new Mochila(); // Se tab de itens, botão vende item.
            venderitem.venderItem();
        }
    }//GEN-LAST:event_tabopActionPerformed

