    private void itemTabStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_itemTabStateChanged
        if (itemTab.getSelectedIndex() == 1) { // Verifica tab atual selecionada
            tabop.setText("Usar Poção"); // Se tab poções, troca textos para uso de itens.
            tabop.setToolTipText("Usar poção para recuperar vida.");
            tabop.setVisible(true);
            btcomprarpocao.setVisible(true);
        } else {
            tabop.setText("Vender Item"); // Se tab itens, troca textos para venda de itens.
            tabop.setToolTipText("Ganho de 50 moedas.");
            tabop.setVisible(true);
            btcomprarpocao.setVisible(false);
        }
    }//GEN-LAST:event_itemTabStateChanged

